import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Heading,
  VStack,
  Input,
  Image,
  TableContainer,
  Table,
  Tbody,
  Td,
  Th,
  Thead,
  Tr,
} from '@chakra-ui/react';
import axios from 'axios';

function App() {
  const [coins, setCoins] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchCoins = async () => {
      try {
        const response = await axios.get(
          'https://api.coingecko.com/api/v3/coins/markets',
          {
            params: {
              vs_currency: 'usd',
              order: 'market_cap_desc',
              per_page: 100,
              page: 1,
              sparkline: false
            }
          }
        );
        setCoins(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchCoins();
    const interval = setInterval(fetchCoins, 60000); // Update every minute
    return () => clearInterval(interval);
  }, []);

  const filteredCoins = coins.filter(coin =>
    coin.name.toLowerCase().includes(search.toLowerCase()) ||
    coin.symbol.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container maxW="container.xl" py={8}>
      <VStack spacing={6}>
        <Heading>Kripto Para Takipçisi</Heading>
        <Input
          placeholder="Kripto para ara..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          size="lg"
          width="100%"
        />
        <TableContainer width="100%">
          <Table variant="simple">
            <Thead>
              <Tr>
                <Th>Logo</Th>
                <Th>İsim</Th>
                <Th>Sembol</Th>
                <Th>Fiyat</Th>
                <Th>24s Değişim</Th>
                <Th>Piyasa Değeri</Th>
              </Tr>
            </Thead>
            <Tbody>
              {filteredCoins.map(coin => (
                <Tr key={coin.id}>
                  <Td>
                    <Image src={coin.image} alt={coin.name} boxSize="24px" />
                  </Td>
                  <Td>{coin.name}</Td>
                  <Td>{coin.symbol.toUpperCase()}</Td>
                  <Td>${coin.current_price.toLocaleString()}</Td>
                  <Td color={coin.price_change_percentage_24h > 0 ? 'green.500' : 'red.500'}>
                    {coin.price_change_percentage_24h.toFixed(2)}%
                  </Td>
                  <Td>${coin.market_cap.toLocaleString()}</Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
        </TableContainer>
      </VStack>
    </Container>
  );
}

export default App;
